CREATE TABLE IF NOT EXISTS Boisson ([d_id] INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, [d_name] varchar(20) not null, [d_sprice] decimal(9,2) not null, [d_pprice] decimal(9,2) not null, [d_stock] smallint not null, [d_stockm] smallint not null,[d_seuil] smallint not null,[d_descr_en] varchar(140) not null,[d_descr_fr] varchar(140) not null,[d_type] varchar(20) not null);
CREATE TABLE IF NOT EXISTS [users] ( [u_id] INTEGER PRIMARY KEY NOT NULL , [u_name] TEXT NOT NULL DEFAULT ( NULL ) , [u_password] TEXT NOT NULL DEFAULT ( NULL ), [u_status] TEXT NOT NULL DEFAULT "user", [u_ismanager] SMALLINT NOT NULL DEFAULT ( 0 ));
CREATE TABLE IF NOT EXISTS [orders] ( [o_id] INTEGER PRIMARY KEY NOT NULL , [b_id] INTEGER NOT NULL, [u_id] INTEGER NOT NULL, [o_amountorder] DOUBLE NOT NULL DEFAULT (0.0), [o_table] SMALLINT NOT NULL, [o_date] DATE NOT NULL, [o_issent] SMALLINT NOT NULL DEFAULT ( 0 ), [o_isserved] SMALLINT NOT NULL DEFAULT ( 0 ), [o_ispaid] SMALLINT NOT NULL DEFAULT (0 ));
CREATE TABLE IF NOT EXISTS [details] ( [det_id] INTEGER PRIMARY KEY NOT NULL , [det_quant] SMALLINT NOT NULL , [d_id] INTEGER NOT NULL , [o_id] INTEGER NOT NULL );
CREATE TABLE IF NOT EXISTS [timetables] ( [t_id] INTEGER PRIMARY KEY NOT NULL , [b_id] SMALLINT NOT NULL, [t_day] INTEGER NOT NULL, [t_begin] TEXT DEFAULT ( NULL ), [t_end] TEXT DEFAULT ( NULL ));